import './RouteModal.css';
import Modal from '../UI/Modal';

function RouteModal(props) {
  const thumbnailOrientation = props.thumbnailIsPortrait
    ? 'portrait'
    : 'landscape';

  return (
    <Modal className="">
      <h2 className="">{props.name}</h2>
      <img className="" src={props.thumbnailSrc} alt={props.name + ' photo'} />
      <div className="">
        <div className="">{props.distance.toFixed(1)} mi</div>
        <div className="">{props.location}</div>
        <div className="">{props.surface.join(', ')}</div>
        <div className="">{props.features.join(', ')}</div>
        <div className="">{props.type}</div>
      </div>
    </Modal>
  );
}

export default RouteModal;
